mapper.select:
SELECT t1.ID , t1.PRODUCT_NAME , t1.PRODUCT_PRICE , t1.DELIVERY_PRICE , t1.ADD_DELIVERY_PRICE , t1.TAGS , t1.OUTBOUND_DAYS , t1.SELLER_ID , t1.CATEGORY_ID , t1.CREATED_DATE , t2.CATEGORY1 , t2.CATEGORY2 , t2.CATEGORY3 , t3.IMAGE_PATH FROM T_PRODUCT t1, T_CATEGORY t2, T_IMAGE t3 WHERE t1.CATEGORY_ID = t2.ID AND t1.ID = t3.PRODUCT_ID
AND t3.TYPE = #{type}
AND t1.ID = #{id}

SELECT TYPE , IMAGE_PATH FROM T_IMAGE WHERE 1 = 1
AND ID = #{id}
AND t3.TYPE = #{type}

mapper.insert:
INSERT INTO T_PRODUCT(PRODUCT_NAME, PRODUCT_PRICE, DELIVERY_PRICE, ADD_DELIVERY_PRICE, TAGS,
	OUTBOUND_DAYS, SELLER_ID, CATEGORY_ID)
VALUES (
	#{productName}
	, #{productPrice}
	, #{deliveryPrice}
	, #{addDeliveryPrice}
	, #{tags}
	, #{outbound_days}
	, #{sellerId}
	, #{categoryId}
)

INSERT INTO T_IMAGE(PRODUCT_ID, TYPE, PATH)
VALUES (
	#{productId}
	, #{type}
	, #{path}
)
